mapper.select:
SELECT d.codpar, d.codp, d.codmat, d.gestion, d.ids, mt.nombre AS nombreMateria, p.nombre AS nombreParalelo, CONCAT(per.nombre, ' ', per.ap, ' ', COALESCE(per.am, '')) AS nombreDocente, u.email AS emailDocente, per.tipo AS tipoDocente, mt.codn, n.nombre AS nombreNivel FROM academico.dicta d INNER JOIN academico.materia mt ON d.codmat = mt.codmat INNER JOIN academico.paralelo p ON d.codpar = p.codp INNER JOIN public.sys_user u ON d.ids = u.id INNER JOIN academico.personal per ON d.ids = per.idusuario LEFT JOIN academico.niveles n ON mt.codn = n.codn
AND d.gestion = #{gestion}
AND d.codmat = #{codmat}
AND mt.codn = #{codn}
AND ( mt.nombre ILIKE CONCAT('%', #{blurry}, '%') OR p.nombre ILIKE CONCAT('%', #{blurry}, '%') OR per.nombre ILIKE CONCAT('%', #{blurry}, '%') OR per.ap ILIKE CONCAT('%', #{blurry}, '%') OR per.am ILIKE CONCAT('%', #{blurry}, '%') OR mt.codmat ILIKE CONCAT('%', #{blurry}, '%') )
ORDER BY mt.nombre ASC, p.nombre ASC

SELECT COUNT(*)
FROM academico.dicta
WHERE codmat = #{codmat}
  AND codpar = #{codpar}
  AND gestion = #{gestion}
